import React, { useEffect, useState } from "react";
import { filterByType, filterByName, orderName, orderByAttack, getTypes, filterByCreated } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";
import './Nav.css';


export default function Nav() {

    const dispatch = useDispatch();
    const [name, setName] = useState('');
    const allTypes = useSelector(state => state.types)
    const [order, setOrder] = useState('');

    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])

    const handleFilterTypes = (e) => {
        dispatch(filterByType(e.target.value))
    }

    const handleOrderName = (e) => {
        e.preventDefault()
        dispatch(orderName(e.target.value))
        setOrder(e.target.value)
    }

    const handleOrderAttack = (e) => {
        e.preventDefault()
        dispatch(orderByAttack(e.target.value))
        setOrder(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(filterByName(name))
        setName('');
    }

    const handleFilterName = (e) => {
        e.preventDefault()
        setName(e.target.value)
    }

    const handleFilterCreated = (e) => {
        e.preventDefault()
        dispatch(filterByCreated(e.target.value));
    }

    return (
        <nav className="navnav">
            <form onSubmit={e => handleSubmit(e)}>
                <input onChange={(e) => handleFilterName(e)} type="text" placeholder="Pokemon Name" value={name} />
                <button type="submit">Buscar</button>
            </form>
            <select onChange={e => handleFilterTypes(e)}>
                <option value='All'>All</option>
                {allTypes?.map(t => {
                    return (
                        <option key={t.id} value={t.name}>{t.name.toUpperCase()}</option>
                    )
                })}
            </select>
            <select onChange={e => handleFilterCreated(e)}>
                <option value='all'>All</option>
                <option value='created'>Created</option>
                <option value= 'api'>Api</option>
            </select>
            <select >
                <option>Alphabetically</option>
                <option onClick={e => handleOrderName(e)} value='asc'>A-Z</option>
                <option onClick={e => handleOrderName(e)} value='desc'>Z-A</option>
            </select>
            <select>
                <option>Order By Attack</option>
                <option onClick={e => handleOrderAttack(e)} value='asc'>++</option>
                <option onClick={e => handleOrderAttack(e)} value='desc'>--</option>
            </select>
        </nav>
    )
}
