import axios from "axios";
export const GET_POKEMONS = "GET_POKEMONS";




export const getPokemons = () => {
    return async function (dispatch) {
        const info = await axios.get("http://localhost:3001/pokemons")
        dispatch({
            type: GET_POKEMONS,
            payload: info.data
        })
    }
}
