import { FILTER_BY_TYPE, GET_DETAIL, GET_POKEMONS } from "./actions";


const initialState = {
    pokemons: [],
    detailPokemon: {}
}

const rootReducer = ( state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload
            }
        case GET_DETAIL:
            return {
                ...state,
                detailPokemon: action.payload
            }
        case FILTER_BY_TYPE:
            const allPokemons = state.pokemons
            const typeFiltered = action.payload === 'All'? allPokemons : allPokemons.filter(p => p.types.name.includes(action.payload))
            return {
                ...state,
                pokemons: typeFiltered
            }
            
        default:
            return {
                ...state
            }
    }
}


export default rootReducer;
