import { FILTER_BY_NAME, FILTER_BY_TYPE, GET_DETAIL, GET_POKEMONS, GET_TYPES, ORDER_BY_ATTACK, ORDER_NAME } from "./actions";


const initialState = {
    pokemons: [],
    detailPokemon: {},
    allPokemons: [],
    types: []
}

const rootReducer = ( state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload
            }
        case GET_DETAIL:
            return {
                ...state,
                detailPokemon: action.payload
            }
        case FILTER_BY_TYPE:
            const allPokemons = state.pokemons
            const typeFiltered = action.payload === 'All'? allPokemons : allPokemons.filter(p => p.types.some(e => e.name === action.payload))
            return {
                ...state,
                pokemons: typeFiltered
            }

        case FILTER_BY_NAME:
            return {
                ...state,
                pokemons: action.payload
            }
        case ORDER_NAME:
            let pokename = state.pokemons;
            let nameOrder = action.payload === 'A-Z' ?
                pokename.sort((a, b) => {
                    return a.name.toLowerCase().localeCompare(b.name.toLowerCase())
                }) :
                pokename.sort((a, b) => {
                    return b.name.toLowerCase().localeCompare(a.name.toLowerCase())
                })
            return {
                ...state,
                pokemons: nameOrder

            }
        case ORDER_BY_ATTACK:
            let pokeAttack = state.pokemons
            let orderAttack = action.payload === '++' ? 
                pokeAttack.sort((a, b) => a.attack - b.attack) :
                pokeAttack.sort((a, b) => b.attack - a.attack);
            return {
                ...state,
                pokemons: orderAttack
            }
            
        default:
            return {
                ...state
            }
    }
}


export default rootReducer;
