import React, { useState } from "react";
import Pokemon from "../Pokemon/Pokemon";
import { useSelector, useDispatch } from 'react-redux'
import { useEffect } from "react";
import { getPokemons } from "../../redux/actions";
import Paged from "../Paged.js/Paged";
import './Home.css'

export default function Home () {

    const dispatch = useDispatch()
    const pokemons = useSelector( state => state.pokemons)

    const [currentPage, setCurrentPage] = useState(1);
    const [pokePerPage, setPokePerPage] = useState(12);
    const indexOfLastPoke = currentPage * pokePerPage;
    const indexOfFirstPoke = indexOfLastPoke - pokePerPage;
    const currentPokemons = pokemons.slice(indexOfFirstPoke, indexOfLastPoke);
    
    
    const paged = (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    useEffect(() => {
        dispatch(getPokemons())
    },[dispatch])

    return (
        <div className="home">
            { pokemons.length && pokemons.map(p => {

                return (   
                    <Pokemon
                    key={p.id}
                    img={p.img}
                    name={p.name}
                    types={p.types.map(t => {
                        return (
                            ' ' + t.name
                        )
                    })}
                    />
            )
                }
                )}
            <Paged 
            pokePerPage={pokePerPage}
            pokemons={pokemons.length}
            paged={paged}/>
        </div>

    )
}
