import { CREATE_POKEMON, FILTER_BY_CREATED, FILTER_BY_NAME, FILTER_BY_TYPE, GET_DETAIL, GET_POKEMONS, GET_TYPES, ORDER_BY_ATTACK, ORDER_NAME } from "./actions";


const initialState = {
    pokemons: [],
    allPokemons: [],
    detailPokemon: {},
    types: []
}

const rootReducer = ( state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload
            }
        case GET_DETAIL:
            return {
                ...state,
                detailPokemon: action.payload
            }
        case FILTER_BY_TYPE:
            const allPokemons = state.allPokemons
            const typeFiltered = action.payload === 'All'? allPokemons : allPokemons.filter(p => p.types.some(e => e.name === action.payload))
            return {
                ...state,
                pokemons: typeFiltered
            }

        case FILTER_BY_NAME:
            return {
                ...state,
                pokemons: action.payload
            }
        
        case FILTER_BY_CREATED:
            let pokeCreated = state.allPokemons;
            let filterCreated;
            if(action.payload === 'created'){
               filterCreated = pokeCreated.filter(p => p.createdInDb) 
            } else if (action.payload === 'api'){
                filterCreated = pokeCreated.filter(p => !p.createdInDb);
            } else {
                filterCreated = pokeCreated;
            }

            return {
                ...state,
                pokemons: filterCreated
            }
        case ORDER_NAME:
            let pokename = state.pokemons;
            let nameOrder = action.payload === 'asc' ?
                pokename.sort((a, b) => {
                    return a.name.toLowerCase().localeCompare(b.name.toLowerCase())
                }) :
                pokename.sort((a, b) => {
                    return b.name.toLowerCase().localeCompare(a.name.toLowerCase())
                })
            return {
                ...state,
                pokemons: nameOrder

            }
        case ORDER_BY_ATTACK:
            let pokeAttack = state.pokemons
            let orderAttack = action.payload === 'asc' ? 
                pokeAttack.sort((a, b) => a.attack - b.attack) :
                pokeAttack.sort((a, b) => b.attack - a.attack);
            return {
                ...state,
                pokemons: orderAttack
            }
        case CREATE_POKEMON:
            return {
                ...state
            }
            
        default:
            return {
                ...state
            }
    }
}


export default rootReducer;
